	id_client SMALLINT := 0;
BEGIN
	SELECT C.ID
	INTO id_client
	FROM CLIENT C INNER JOIN ORD O
	ON C.ID = O.ID_CLIENT
	INNER JOIN ORD_DETAIL OD
	ON OD.ID_ORDER = O.ID_CLIENT
	WHERE OD.ID = 1
	LIMIT 1;

