		SET STOCK = ((SELECT STOCK FROM FORMAT WHERE ID = NEW.ID_FORMAT) - (NEW.AMOUNT))
		WHERE ID = NEW.ID_FORMAT;
	END IF;

	RETURN NEW;
END;
$$;


